mapper.select:
SELECT id,title,summary,created_person,created_date,status FROM knowledgebase WHERE (created_person LIKE '%' || #{keyword} || '%' OR title LIKE '%' || #{keyword} || '%' OR summary LIKE '%' || #{keyword} || '%')
AND status = #{status}
ORDER BY created_date

mapper.update:
UPDATE knowledgebase
SET title = #{title},
    summary = #{summary},
    content = #{content},
    status = #{status}
WHERE id = #{id}

UPDATE knowledgebase
SET status = 1
WHERE id = #{id}

mapper.select:
SELECT title,summary,content FROM knowledgebase
WHERE id = #{id}
ORDER BY created_date
